 val name: String,
 val ctxSize: Int,
 val temperature: Float,
 val topP: Float,
 val supportsTools: Boolean = false
)

fun OpenRouterModel.toModelData(): ModelData {
 return ModelData(
 id = id,
 modelName = name,
 providerName = ModelProvider.OpenRouter.toString(),
 modelUrl = id,
 ctxSize = ctxSize,
 temp = temperature,
 topP = topP,
 isToolCalling = supportsTools
 )
}

